import { useState, useEffect } from "react"
const TestComponent = (props) => {
    const [count, setCount] = useState(0)

    useEffect(() => {
        console.log("call every time when component render/rerender")
    })

    useEffect(() => {
        console.log("Perform action")
    }, [])

    useEffect(() => {
        console.log("call only when component changes first time");
        () => {
            console.log("clean up function");
        }
    }, [])

    useEffect(() => {
        console.log("call only when component render first time or count changes");
    }, [count]);
    return <div>Hello {count}</div>
}
export default TestComponent;
