import React from 'react';
import './Products.css'
import Card from 'react-bootstrap/Card';
import GetProductList from '../APIcalls/GetProductList';
import { useState } from 'react';
function ProductList () {
    const {loading, productsList} = GetProductList();
    const [searchProducts, setSearchProducts] = useState([]);


    console.log(productsList);
    if (loading) {
        return <div>Loading...</div>;
    }
    return (
        <div>
        <h1>Fake Store Products</h1>
            <div className="products">
                {productsList['products'].map
                (({id, thumbnail, title, brand, description, category, price, rating}) => (
                    <Card key={id} className="product" style={{ width: '18rem' }}>
                        <Card.Img variant="top" src={thumbnail} />
                        <Card.Body>
                            <Card.Title>{title}</Card.Title>
                            <Card.Text>
                                {description}
                            </Card.Text>
                            <Card.Text>
                                Brand - {brand}
                            </Card.Text>
                            <Card.Text>
                                Category - {category}
                            </Card.Text>
                            <Card.Text>
                                Price - {price}
                            </Card.Text>
                            <Card.Text>
                                Rating - {rating}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                ))}
            </div>   
        </div>
    );
}
export default ProductList;
