import React from 'react';
import './Products.css'
import GetProductList from '../APIcalls/GetProductList';
import { useState } from 'react';
import SearchProducts from '../SearchFilter/SearchProducts';
import DisplayCard from '../DisplayComponent/DisplayComponent';
function ProductList () {
    const {loading, productsList} = GetProductList();
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [search, setSearch] = useState("");
    if(!productsList){
        alert("No products found");
        return null;
    }

    if (loading) {
        return <div className="loader"></div>
    }
    return (
        <div>
        <h1>Fake Store Products</h1>
        <SearchProducts products = {productsList} setFilteredProducts={setFilteredProducts} setSearch={setSearch}/>
            <div className="products">
                {filteredProducts.map(({id, thumbnail, title, brand, description, category, price, rating}) => {
                    console.log(title.toLowerCase().includes(search))
                    if(title.toLowerCase().includes(search)){
                    return <DisplayCard 
                        key={id} 
                        id={id} 
                        thumbnail={thumbnail} 
                        title={title} 
                        brand={brand} 
                        description={description} 
                        category={category} 
                        price={price} 
                        rating={rating} />
                    }
        })}
            </div>   
        </div>
    );
}
export default ProductList;
